const crypto = require('crypto');

function encryptPassword(text, secretKey) {
  const generatedBytes = crypto.randomBytes(16);
  const krypto = crypto.createCipheriv('aes-256-cbc', secretKey, iv);
  let encrypted = krypto.update(text, 'utf8', 'hex');
  encrypted += krypto.final('hex');
  return {
    encrypted: encrypted,
    generatedBytes: generatedBytes.toString('hex')
  };
}

function decryptPassword(encryptedData, secretKey) {
  const kryptoDecipher = crypto.createDecipheriv('aes-256-cbc', secretKey, Buffer.from(encryptedData.iv, 'hex'));
  let decrypted = kryptoDecipher.update(encryptedData.encrypted, 'hex', 'utf8');
  decrypted += kryptoDecipher.final('utf8');
  return decrypted;
}

module.exports = {
  encryptPassword,
  decryptPassword
};
